import React, { useState, useEffect, useCallback } from 'react';
import { useLocationData } from '../App';
import './HomePage.css';

// Popular Indian Cities
const POPULAR_CITIES = [
    { name: 'Delhi', lat: 28.6139, lon: 77.2090 },
    { name: 'Mumbai', lat: 19.0760, lon: 72.8777 },
    { name: 'Bangalore', lat: 12.9716, lon: 77.5946 },
    { name: 'Hyderabad', lat: 17.3850, lon: 78.4867 },
    { name: 'Chennai', lat: 13.0827, lon: 80.2707 },
    { name: 'Kolkata', lat: 22.5726, lon: 88.3639 },
    { name: 'Pune', lat: 18.5204, lon: 73.8567 },
    { name: 'Ahmedabad', lat: 23.0225, lon: 72.5714 },
    { name: 'Jaipur', lat: 26.9124, lon: 75.7873 },
    { name: 'Lucknow', lat: 26.8467, lon: 80.9462 },
];

// AQI Color Helper
const getAQIInfo = (aqi) => {
    if (aqi <= 50) return { level: 'good', color: '#00e400', bg: 'rgba(0,228,0,0.15)', label: 'Good', emoji: 'ðŸ˜Š' };
    if (aqi <= 100) return { level: 'moderate', color: '#ffff00', bg: 'rgba(255,255,0,0.15)', label: 'Moderate', emoji: 'ðŸ™‚' };
    if (aqi <= 150) return { level: 'sensitive', color: '#ff7e00', bg: 'rgba(255,126,0,0.15)', label: 'Unhealthy for Sensitive', emoji: 'ðŸ˜·' };
    if (aqi <= 200) return { level: 'unhealthy', color: '#ff0000', bg: 'rgba(255,0,0,0.15)', label: 'Unhealthy', emoji: 'ðŸ¤¢' };
    if (aqi <= 300) return { level: 'very-unhealthy', color: '#8f3f97', bg: 'rgba(143,63,151,0.15)', label: 'Very Unhealthy', emoji: 'ðŸ¤®' };
    return { level: 'hazardous', color: '#7e0023', bg: 'rgba(126,0,35,0.15)', label: 'Hazardous', emoji: 'ðŸ’€' };
};

// Mini Trend Chart Component
const TrendChart = ({ data, color }) => {
    if (!data || data.length === 0) return null;

    const max = Math.max(...data);
    const min = Math.min(...data);
    const range = max - min || 1;

    const points = data.map((val, i) => {
        const x = (i / (data.length - 1)) * 100;
        const y = 100 - ((val - min) / range) * 80;
        return `${x},${y}`;
    }).join(' ');

    return (
        <svg viewBox="0 0 100 100" className="trend-chart">
            <defs>
                <linearGradient id={`grad-${color}`} x1="0%" y1="0%" x2="0%" y2="100%">
                    <stop offset="0%" stopColor={color} stopOpacity="0.3" />
                    <stop offset="100%" stopColor={color} stopOpacity="0" />
                </linearGradient>
            </defs>
            <polyline
                points={points}
                fill="none"
                stroke={color}
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
            />
            <polygon
                points={`0,100 ${points} 100,100`}
                fill={`url(#grad-${color})`}
            />
        </svg>
    );
};

function HomePage() {
    const { userLocation, locationName, isLoadingLocation, API_URL, requestLocation } = useLocationData();
    const [currentData, setCurrentData] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedCity, setSelectedCity] = useState(null);
    const [citySearch, setCitySearch] = useState('');
    const [savedCities, setSavedCities] = useState([]);
    const [cityData, setCityData] = useState({});
    const [showCityPanel, setShowCityPanel] = useState(false);
    const [trendData, setTrendData] = useState([]);

    // Fetch data for a location
    const fetchLocationData = useCallback(async (lat, lon, isMain = false) => {
        try {
            const [currentRes, predRes] = await Promise.all([
                fetch(`${API_URL}/current?lat=${lat}&lon=${lon}`),
                fetch(`${API_URL}/predict?lat=${lat}&lon=${lon}`)
            ]);

            const currentJson = await currentRes.json();
            const predJson = await predRes.json();

            if (isMain) {
                setCurrentData(currentJson);
                setPrediction(predJson);
                // Generate mock trend data (in real app, fetch from historical API)
                const mockTrend = Array.from({ length: 24 }, () =>
                    Math.max(20, currentJson.aqi + Math.floor(Math.random() * 40 - 20))
                );
                setTrendData(mockTrend);
            }

            return { current: currentJson, prediction: predJson };
        } catch (err) {
            console.error('Fetch error:', err);
            return null;
        }
    }, [API_URL]);

    // Initial load with user location
    useEffect(() => {
        if (userLocation) {
            setLoading(true);
            fetchLocationData(userLocation.lat, userLocation.lon, true)
                .finally(() => setLoading(false));
        }
    }, [userLocation, fetchLocationData]);

    // Fetch data for saved cities
    useEffect(() => {
        savedCities.forEach(async (city) => {
            if (!cityData[city.name]) {
                const data = await fetchLocationData(city.lat, city.lon);
                if (data) {
                    setCityData(prev => ({ ...prev, [city.name]: data }));
                }
            }
        });
    }, [savedCities, fetchLocationData, cityData]);

    // Add city
    const addCity = (city) => {
        if (!savedCities.find(c => c.name === city.name)) {
            setSavedCities(prev => [...prev, city]);
        }
        setCitySearch('');
    };

    // Remove city
    const removeCity = (cityName) => {
        setSavedCities(prev => prev.filter(c => c.name !== cityName));
        setCityData(prev => {
            const newData = { ...prev };
            delete newData[cityName];
            return newData;
        });
    };

    // Select city to view
    const selectCity = async (city) => {
        setSelectedCity(city);
        setLoading(true);
        await fetchLocationData(city.lat, city.lon, true);
        setLoading(false);
    };

    // Back to my location
    const backToMyLocation = async () => {
        setSelectedCity(null);
        if (userLocation) {
            setLoading(true);
            await fetchLocationData(userLocation.lat, userLocation.lon, true);
            setLoading(false);
        }
    };

    const filteredCities = POPULAR_CITIES.filter(city =>
        city.name.toLowerCase().includes(citySearch.toLowerCase()) &&
        !savedCities.find(c => c.name === city.name)
    );

    const aqi = currentData?.aqi || 0;
    const aqiInfo = getAQIInfo(aqi);
    const weather = currentData?.weather || {};
    const displayName = selectedCity ? selectedCity.name : locationName;

    if (loading && !currentData) {
        return (
            <div className="home-page">
                <div className="loading-screen">
                    <div className="loader"></div>
                    <p>Detecting your location...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="home-page">
            {/* Sidebar - City List */}
            <aside className={`city-sidebar ${showCityPanel ? 'open' : ''}`}>
                <div className="sidebar-header">
                    <h3>ðŸ“ Locations</h3>
                    <button className="close-btn" onClick={() => setShowCityPanel(false)}>Ã—</button>
                </div>

                {/* My Location */}
                <div
                    className={`city-item my-location ${!selectedCity ? 'active' : ''}`}
                    onClick={backToMyLocation}
                >
                    <div className="city-info">
                        <span className="city-icon">ðŸ </span>
                        <div>
                            <span className="city-name">{locationName}</span>
                            <span className="city-label">My Location</span>
                        </div>
                    </div>
                    <span className="city-aqi" style={{ color: getAQIInfo(currentData?.aqi || 0).color }}>
                        {currentData?.aqi || '--'}
                    </span>
                </div>

                {/* Saved Cities */}
                {savedCities.map(city => {
                    const data = cityData[city.name];
                    const cityAqi = data?.current?.aqi || 0;
                    const info = getAQIInfo(cityAqi);
                    return (
                        <div
                            key={city.name}
                            className={`city-item ${selectedCity?.name === city.name ? 'active' : ''}`}
                            onClick={() => selectCity(city)}
                        >
                            <div className="city-info">
                                <span className="city-icon">ðŸ™ï¸</span>
                                <div>
                                    <span className="city-name">{city.name}</span>
                                    <span className="city-label">{info.label}</span>
                                </div>
                            </div>
                            <div className="city-aqi-wrap">
                                <span className="city-aqi" style={{ color: info.color }}>{cityAqi || '--'}</span>
                                <button className="remove-btn" onClick={(e) => { e.stopPropagation(); removeCity(city.name); }}>Ã—</button>
                            </div>
                        </div>
                    );
                })}

                {/* Add City */}
                <div className="add-city-section">
                    <input
                        type="text"
                        placeholder="Search city..."
                        value={citySearch}
                        onChange={(e) => setCitySearch(e.target.value)}
                        className="city-search"
                    />
                    {citySearch && (
                        <div className="city-suggestions">
                            {filteredCities.map(city => (
                                <div key={city.name} className="suggestion-item" onClick={() => addCity(city)}>
                                    <span>ðŸ™ï¸ {city.name}</span>
                                    <span className="add-icon">+</span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </aside>

            {/* Mobile Toggle */}
            <button className="mobile-city-toggle" onClick={() => setShowCityPanel(true)}>
                ðŸ“ Cities
            </button>

            {/* Main Content */}
            <main className="main-content">
                {/* Header */}
                <header className="page-header">
                    <div className="header-left">
                        <div className="live-badge">
                            <span className="pulse"></span>
                            LIVE
                        </div>
                        <div className="location-info">
                            <h1>{displayName}</h1>
                            <p className="station-name">ðŸ“ {currentData?.station || 'Nearest Station'}</p>
                        </div>
                    </div>
                    <div className="header-right">
                        <button className="locate-btn" onClick={requestLocation}>
                            ðŸŽ¯ Locate Me
                        </button>
                        <button className="refresh-btn" onClick={() => fetchLocationData(
                            selectedCity?.lat || userLocation?.lat,
                            selectedCity?.lon || userLocation?.lon,
                            true
                        )}>
                            ðŸ”„ Refresh
                        </button>
                    </div>
                </header>

                {/* AQI Hero Section */}
                <section className="aqi-hero" style={{ background: aqiInfo.bg }}>
                    <div className="hero-left">
                        <div className="aqi-value-block">
                            <span className="aqi-number" style={{ color: aqiInfo.color }}>{aqi}</span>
                            <span className="aqi-label">AQI (US)</span>
                        </div>
                        <div className="aqi-category" style={{ background: aqiInfo.color }}>
                            {aqiInfo.emoji} {aqiInfo.label}
                        </div>
                        <div className="pollutant-values">
                            <div className="poll-item">
                                <span className="poll-name">PM2.5</span>
                                <span className="poll-val">{currentData?.pm25 || 0} Âµg/mÂ³</span>
                            </div>
                            <div className="poll-item">
                                <span className="poll-name">PM10</span>
                                <span className="poll-val">{currentData?.pm10 || 0} Âµg/mÂ³</span>
                            </div>
                        </div>
                    </div>

                    <div className="hero-center">
                        <div className="character-emoji">{aqiInfo.emoji}</div>
                    </div>

                    <div className="hero-right">
                        <div className="weather-block">
                            <div className="weather-main">
                                <span className="weather-icon">{
                                    weather.weather?.includes('Rain') ? 'ðŸŒ§ï¸' :
                                        weather.weather?.includes('Cloud') ? 'â˜ï¸' :
                                            weather.weather?.includes('Haze') ? 'ðŸŒ«ï¸' :
                                                weather.weather?.includes('Clear') ? 'â˜€ï¸' : 'ðŸŒ¤ï¸'
                                }</span>
                                <span className="weather-temp">{weather.temperature || 25}Â°C</span>
                            </div>
                            <span className="weather-desc">{weather.weather || 'Sunny'}</span>
                            <div className="weather-stats">
                                <span>ðŸ’§ {weather.humidity || 60}%</span>
                                <span>ðŸ’¨ {weather.wind_speed || 10} km/h</span>
                                <span>â˜€ï¸ UV {weather.uv_index || 0}</span>
                            </div>
                        </div>
                    </div>
                </section>

                {/* Trend & Prediction Section */}
                <section className="trend-prediction-section">
                    {/* 24h Trend */}
                    <div className="trend-card">
                        <h3>ðŸ“ˆ 24 Hour AQI Trend - {displayName}</h3>
                        <div className="trend-chart-container">
                            <TrendChart data={trendData} color={aqiInfo.color} />
                            <div className="trend-labels">
                                <span>Now</span>
                                <span>6h ago</span>
                                <span>12h ago</span>
                                <span>18h ago</span>
                                <span>24h ago</span>
                            </div>
                        </div>
                        <div className="trend-stats">
                            <div className="trend-stat">
                                <span className="stat-label">Current</span>
                                <span className="stat-value" style={{ color: aqiInfo.color }}>{aqi}</span>
                            </div>
                            <div className="trend-stat">
                                <span className="stat-label">24h Avg</span>
                                <span className="stat-value">{Math.round(trendData.reduce((a, b) => a + b, 0) / trendData.length) || aqi}</span>
                            </div>
                            <div className="trend-stat">
                                <span className="stat-label">Max</span>
                                <span className="stat-value">{Math.max(...trendData) || aqi}</span>
                            </div>
                            <div className="trend-stat">
                                <span className="stat-label">Min</span>
                                <span className="stat-value">{Math.min(...trendData) || aqi}</span>
                            </div>
                        </div>
                    </div>

                    {/* Prediction */}
                    <div className="prediction-card">
                        <h3>ðŸ”® AQI Prediction</h3>
                        <div className="prediction-content">
                            <div className="pred-main">
                                <span className="pred-value" style={{ color: getAQIInfo(prediction?.AQI_Prediction || aqi).color }}>
                                    {prediction?.AQI_Prediction || aqi}
                                </span>
                                <span className="pred-label">Next Hour</span>
                            </div>
                            <div className="pred-category" style={{ background: getAQIInfo(prediction?.AQI_Prediction || aqi).color }}>
                                {getAQIInfo(prediction?.AQI_Prediction || aqi).emoji} {prediction?.category || aqiInfo.label}
                            </div>
                            <div className="pred-model">
                                {prediction?.model_trained ? 'ðŸ§¬ GA-KELM Model' : 'ðŸ“Š EPA Formula'}
                            </div>
                            <div className="pred-change">
                                {prediction?.AQI_Prediction > aqi ? (
                                    <span className="change-up">â†— +{prediction.AQI_Prediction - aqi} expected</span>
                                ) : prediction?.AQI_Prediction < aqi ? (
                                    <span className="change-down">â†˜ {prediction.AQI_Prediction - aqi} expected</span>
                                ) : (
                                    <span className="change-same">â†’ Stable</span>
                                )}
                            </div>
                        </div>
                    </div>
                </section>

                {/* Pollutants Grid */}
                <section className="pollutants-section">
                    <h3>ðŸ§ª Pollutant Breakdown</h3>
                    <div className="pollutants-grid">
                        {[
                            { name: 'PM2.5', value: currentData?.pm25, unit: 'Âµg/mÂ³', icon: 'ðŸ”´' },
                            { name: 'PM10', value: currentData?.pm10, unit: 'Âµg/mÂ³', icon: 'ðŸŸ ' },
                            { name: 'NOâ‚‚', value: currentData?.no2, unit: 'Âµg/mÂ³', icon: 'ðŸŸ¡' },
                            { name: 'Oâ‚ƒ', value: currentData?.o3, unit: 'Âµg/mÂ³', icon: 'ðŸŸ¢' },
                            { name: 'SOâ‚‚', value: currentData?.so2, unit: 'Âµg/mÂ³', icon: 'ðŸ”µ' },
                            { name: 'CO', value: currentData?.co, unit: 'mg/mÂ³', icon: 'ðŸŸ£' },
                        ].map(poll => (
                            <div key={poll.name} className="pollutant-card">
                                <span className="poll-icon">{poll.icon}</span>
                                <span className="poll-name">{poll.name}</span>
                                <span className="poll-value">{poll.value || 0}</span>
                                <span className="poll-unit">{poll.unit}</span>
                            </div>
                        ))}
                    </div>
                </section>

                {/* City Comparison */}
                {savedCities.length > 0 && (
                    <section className="comparison-section">
                        <h3>ðŸ™ï¸ City Comparison</h3>
                        <div className="comparison-grid">
                            {/* My Location */}
                            <div className="comp-card">
                                <span className="comp-name">ðŸ“ {locationName}</span>
                                <span className="comp-aqi" style={{ color: getAQIInfo(currentData?.aqi || 0).color }}>
                                    {currentData?.aqi || '--'}
                                </span>
                                <span className="comp-label">{getAQIInfo(currentData?.aqi || 0).label}</span>
                            </div>

                            {savedCities.slice(0, 4).map(city => {
                                const data = cityData[city.name];
                                const cityAqi = data?.current?.aqi || 0;
                                const info = getAQIInfo(cityAqi);
                                return (
                                    <div key={city.name} className="comp-card" onClick={() => selectCity(city)}>
                                        <span className="comp-name">ðŸ™ï¸ {city.name}</span>
                                        <span className="comp-aqi" style={{ color: info.color }}>{cityAqi || '--'}</span>
                                        <span className="comp-label">{info.label}</span>
                                    </div>
                                );
                            })}
                        </div>
                    </section>
                )}

                {/* Footer */}
                <footer className="home-footer">
                    <p>Powered by <strong>GA-KELM Machine Learning</strong> | Data: {currentData?.source || 'WAQI'}</p>
                </footer>
            </main>
        </div>
    );
}

export default HomePage;
