import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useLocationData } from '../App';
import './HomePage.css';

function HomePage() {
    const { userLocation, locationName, isLoadingLocation, API_URL, requestLocation } = useLocationData();
    const [currentData, setCurrentData] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(true);
    const [customLocation, setCustomLocation] = useState({ city: '', country: '', lat: '', lon: '' });
    const [savedLocations, setSavedLocations] = useState([]);

    const fetchData = useCallback(async (lat, lon) => {
        setLoading(true);
        try {
            const [currentRes, predRes] = await Promise.all([
                fetch(`${API_URL}/current?lat=${lat}&lon=${lon}`),
                fetch(`${API_URL}/predict?lat=${lat}&lon=${lon}`)
            ]);
            setCurrentData(await currentRes.json());
            setPrediction(await predRes.json());
        } catch (err) {
            console.error('Error:', err);
        }
        setLoading(false);
    }, [API_URL]);

    useEffect(() => {
        if (userLocation) {
            fetchData(userLocation.lat, userLocation.lon);
        }
    }, [userLocation, fetchData]);

    const handleAddLocation = () => {
        if (customLocation.lat && customLocation.lon) {
            const newLoc = {
                id: Date.now(),
                city: customLocation.city || 'Custom',
                country: customLocation.country || '',
                lat: parseFloat(customLocation.lat),
                lon: parseFloat(customLocation.lon)
            };
            setSavedLocations([...savedLocations, newLoc]);
            fetchData(newLoc.lat, newLoc.lon);
            setCustomLocation({ city: '', country: '', lat: '', lon: '' });
        }
    };

    const handleSelectLocation = (loc) => {
        fetchData(loc.lat, loc.lon);
    };

    const getAQIClass = (aqi) => {
        if (aqi <= 50) return 'good';
        if (aqi <= 100) return 'moderate';
        if (aqi <= 150) return 'sensitive';
        if (aqi <= 200) return 'unhealthy';
        if (aqi <= 300) return 'very-unhealthy';
        return 'hazardous';
    };

    const getAQIEmoji = (aqi) => {
        if (aqi <= 50) return 'üòä';
        if (aqi <= 100) return 'üôÇ';
        if (aqi <= 150) return 'üòê';
        if (aqi <= 200) return 'üò∑';
        if (aqi <= 300) return 'ü§¢';
        return 'üíÄ';
    };

    const getWeatherAnimation = (weather) => {
        const w = weather?.toLowerCase() || 'clear';
        if (w.includes('rain')) return 'rain';
        if (w.includes('cloud')) return 'cloudy';
        if (w.includes('thunder')) return 'storm';
        if (w.includes('snow')) return 'snow';
        if (w.includes('haze') || w.includes('mist') || w.includes('fog')) return 'haze';
        return 'sunny';
    };

    const weather = currentData?.weather || {};
    const aqiClass = currentData ? getAQIClass(currentData.aqi) : 'moderate';
    const weatherAnim = getWeatherAnimation(weather.weather);

    return (
        <div className="home-page">
            {/* Sidebar */}
            <aside className="sidebar">
                <div className="location-form">
                    <h3>üìç Add Location</h3>
                    <div className="form-row">
                        <input
                            type="text"
                            placeholder="City"
                            value={customLocation.city}
                            onChange={(e) => setCustomLocation({ ...customLocation, city: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="Country"
                            value={customLocation.country}
                            onChange={(e) => setCustomLocation({ ...customLocation, country: e.target.value })}
                        />
                    </div>
                    <div className="form-row">
                        <input
                            type="number"
                            placeholder="Latitude *"
                            value={customLocation.lat}
                            onChange={(e) => setCustomLocation({ ...customLocation, lat: e.target.value })}
                        />
                        <input
                            type="number"
                            placeholder="Longitude *"
                            value={customLocation.lon}
                            onChange={(e) => setCustomLocation({ ...customLocation, lon: e.target.value })}
                        />
                    </div>
                    <button className="btn-add" onClick={handleAddLocation}>Add Location</button>
                </div>

                <div className="saved-locations">
                    <h3>üó∫Ô∏è Saved Locations</h3>
                    {savedLocations.length === 0 ? (
                        <div className="no-locations">
                            <span className="icon">üìå</span>
                            <p>No locations added yet</p>
                            <button className="btn-first" onClick={requestLocation}>
                                Use My Location
                            </button>
                        </div>
                    ) : (
                        <div className="locations-list">
                            {savedLocations.map(loc => (
                                <div
                                    key={loc.id}
                                    className="location-item"
                                    onClick={() => handleSelectLocation(loc)}
                                >
                                    <span className="loc-name">{loc.city}</span>
                                    <span className="loc-coords">{loc.lat.toFixed(2)}, {loc.lon.toFixed(2)}</span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                <div className="pollutants-sidebar">
                    <h3>üß™ Pollutants</h3>
                    {currentData && (
                        <div className="pollutants-mini">
                            <div className="poll-item"><span>PM2.5</span><span>{currentData.pm25}</span></div>
                            <div className="poll-item"><span>PM10</span><span>{currentData.pm10}</span></div>
                            <div className="poll-item"><span>O‚ÇÉ</span><span>{currentData.o3}</span></div>
                            <div className="poll-item"><span>NO‚ÇÇ</span><span>{currentData.no2}</span></div>
                            <div className="poll-item"><span>SO‚ÇÇ</span><span>{currentData.so2}</span></div>
                            <div className="poll-item"><span>CO</span><span>{currentData.co}</span></div>
                        </div>
                    )}
                </div>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                {loading || isLoadingLocation ? (
                    <div className="loading-state">
                        <div className="loader"></div>
                        <p>Loading air quality data...</p>
                    </div>
                ) : currentData ? (
                    <>
                        {/* Weather Animation Background */}
                        <div className={`weather-animation ${weatherAnim}`}>
                            {weatherAnim === 'sunny' && (
                                <div className="sun">
                                    <div className="sun-rays"></div>
                                </div>
                            )}
                            {weatherAnim === 'cloudy' && (
                                <>
                                    <div className="cloud cloud-1"></div>
                                    <div className="cloud cloud-2"></div>
                                    <div className="cloud cloud-3"></div>
                                </>
                            )}
                            {weatherAnim === 'rain' && (
                                <>
                                    <div className="cloud cloud-1"></div>
                                    <div className="rain-container">
                                        {[...Array(20)].map((_, i) => (
                                            <div key={i} className="rain-drop" style={{
                                                left: `${Math.random() * 100}%`,
                                                animationDelay: `${Math.random() * 2}s`
                                            }}></div>
                                        ))}
                                    </div>
                                </>
                            )}
                            {weatherAnim === 'haze' && (
                                <div className="haze-overlay">
                                    <div className="haze-layer"></div>
                                    <div className="haze-layer"></div>
                                </div>
                            )}
                        </div>

                        {/* AQI Particles Animation */}
                        <div className={`aqi-particles ${aqiClass}`}>
                            {[...Array(15)].map((_, i) => (
                                <div key={i} className="particle" style={{
                                    left: `${Math.random() * 100}%`,
                                    top: `${Math.random() * 100}%`,
                                    animationDelay: `${Math.random() * 5}s`,
                                    animationDuration: `${3 + Math.random() * 4}s`
                                }}></div>
                            ))}
                        </div>

                        {/* Location Header */}
                        <div className="location-header">
                            <h1>üìç {locationName}</h1>
                            <span className="live-badge">
                                <span className="pulse"></span>
                                Live
                            </span>
                        </div>

                        {/* Main AQI Display */}
                        <div className="aqi-display-grid">
                            <div className={`aqi-main-card ${aqiClass}`}>
                                <div className="aqi-emoji">{getAQIEmoji(currentData.aqi)}</div>
                                <div className="aqi-number">{currentData.aqi}</div>
                                <div className="aqi-label">Air Quality Index</div>
                                <div className={`aqi-category ${aqiClass}`}>{currentData.category}</div>

                                {/* AQI Scale Bar */}
                                <div className="aqi-scale">
                                    <div className="scale-bar">
                                        <div className="scale-indicator" style={{
                                            left: `${Math.min((currentData.aqi / 300) * 100, 100)}%`
                                        }}></div>
                                    </div>
                                    <div className="scale-labels">
                                        <span>Good</span>
                                        <span>Moderate</span>
                                        <span>Unhealthy</span>
                                        <span>Hazardous</span>
                                    </div>
                                </div>
                            </div>

                            {/* Weather Card */}
                            <div className="weather-card">
                                <div className="weather-icon-large">
                                    {weatherAnim === 'sunny' && '‚òÄÔ∏è'}
                                    {weatherAnim === 'cloudy' && '‚òÅÔ∏è'}
                                    {weatherAnim === 'rain' && 'üåßÔ∏è'}
                                    {weatherAnim === 'haze' && 'üå´Ô∏è'}
                                    {weatherAnim === 'storm' && '‚õàÔ∏è'}
                                    {weatherAnim === 'snow' && '‚ùÑÔ∏è'}
                                </div>
                                <div className="weather-temp">{weather.temperature || '--'}¬∞C</div>
                                <div className="weather-desc">{weather.weather || 'Loading...'}</div>
                                <div className="weather-details">
                                    <div><span>üíß</span> {weather.humidity || '--'}%</div>
                                    <div><span>üí®</span> {weather.wind_speed || '--'} km/h</div>
                                    <div><span>üëÅÔ∏è</span> {weather.visibility || '--'} km</div>
                                </div>
                            </div>

                            {/* Prediction Card */}
                            <div className={`prediction-card ${prediction ? getAQIClass(prediction.AQI_Prediction) : 'moderate'}`}>
                                <div className="pred-header">
                                    <span className="pred-icon">üß¨</span>
                                    <span>GA-KELM Prediction</span>
                                </div>
                                <div className="pred-value">{prediction ? Math.round(prediction.AQI_Prediction) : '--'}</div>
                                <div className="pred-category">{prediction?.category || 'Loading...'}</div>
                                <div className="pred-status">
                                    {prediction?.model_trained ? '‚úÖ ML Model' : 'üìê EPA Formula'}
                                </div>
                            </div>
                        </div>

                        {/* Quick Actions */}
                        <div className="quick-actions">
                            <Link to="/current" className="action-btn">
                                <span>üìä</span> Detailed View
                            </Link>
                            <Link to="/prediction" className="action-btn">
                                <span>üîÆ</span> ML Prediction
                            </Link>
                            <button
                                className="action-btn refresh"
                                onClick={() => fetchData(userLocation?.lat, userLocation?.lon)}
                            >
                                <span>üîÑ</span> Refresh
                            </button>
                        </div>
                    </>
                ) : (
                    <div className="error-state">
                        <h2>Unable to load data</h2>
                        <button onClick={requestLocation}>Try Again</button>
                    </div>
                )}
            </main>

            {/* Footer */}
            <footer className="app-footer">
                Real-Time AQI Prediction System ‚Ä¢ Powered by <span className="highlight">GA-KELM</span> Machine Learning
            </footer>
        </div>
    );
}

export default HomePage;
