import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useLocationData } from '../App';
import './HomePage.css';

// AQI Character SVGs based on air quality
const AQICharacter = ({ level }) => {
    const getCharacter = () => {
        if (level <= 50) return { emoji: 'üòä', mood: 'happy', color: '#22c55e' };
        if (level <= 100) return { emoji: 'üòê', mood: 'okay', color: '#eab308' };
        if (level <= 150) return { emoji: 'üò∑', mood: 'mask', color: '#f97316' };
        if (level <= 200) return { emoji: 'ü§¢', mood: 'sick', color: '#ef4444' };
        if (level <= 300) return { emoji: 'ü§Æ', mood: 'very-sick', color: '#a855f7' };
        return { emoji: 'üíÄ', mood: 'hazardous', color: '#991b1b' };
    };

    const char = getCharacter();

    return (
        <div className={`aqi-character ${char.mood}`}>
            <div className="character-body">
                <span className="character-face">{char.emoji}</span>
            </div>
            <div className="character-shadow"></div>
        </div>
    );
};

// AQI Scale Component
const AQIScale = ({ value }) => {
    const position = Math.min(100, (value / 500) * 100);

    return (
        <div className="aqi-scale-container">
            <div className="scale-categories">
                <span className="cat good">Good</span>
                <span className="cat moderate">Moderate</span>
                <span className="cat poor">Poor</span>
                <span className="cat unhealthy">Unhealthy</span>
                <span className="cat severe">Severe</span>
                <span className="cat hazardous">Hazardous</span>
            </div>
            <div className="scale-bar-full">
                <div className="scale-marker" style={{ left: `${position}%` }}>
                    <div className="marker-line"></div>
                    <div className="marker-value">{value}</div>
                </div>
            </div>
            <div className="scale-numbers">
                <span>0</span>
                <span>50</span>
                <span>100</span>
                <span>150</span>
                <span>200</span>
                <span>300</span>
                <span>301+</span>
            </div>
        </div>
    );
};

// Weather Icon Component
const WeatherIcon = ({ weather }) => {
    const icons = {
        'Clear': '‚òÄÔ∏è',
        'Sunny': '‚òÄÔ∏è',
        'Clouds': '‚òÅÔ∏è',
        'Partly Cloudy': '‚õÖ',
        'Rain': 'üåßÔ∏è',
        'Drizzle': 'üå¶Ô∏è',
        'Thunderstorm': '‚õàÔ∏è',
        'Snow': '‚ùÑÔ∏è',
        'Mist': 'üå´Ô∏è',
        'Haze': 'üå´Ô∏è',
        'Fog': 'üå´Ô∏è',
    };
    return <span className="weather-icon-emoji">{icons[weather] || 'üå§Ô∏è'}</span>;
};

function HomePage() {
    const { userLocation, locationName, isLoadingLocation, API_URL, requestLocation } = useLocationData();
    const [currentData, setCurrentData] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [lastUpdated, setLastUpdated] = useState(null);
    const [savedLocations, setSavedLocations] = useState([
        { name: 'Hyderabad', lat: 17.385, lon: 78.4867 }
    ]);
    const [comparisonData, setComparisonData] = useState(null);

    const fetchData = useCallback(async (lat, lon) => {
        setLoading(true);
        setError(null);
        try {
            const [currentRes, predRes] = await Promise.all([
                fetch(`${API_URL}/current?lat=${lat}&lon=${lon}`),
                fetch(`${API_URL}/predict?lat=${lat}&lon=${lon}`)
            ]);

            if (!currentRes.ok || !predRes.ok) throw new Error('API Error');

            const currentJson = await currentRes.json();
            const predJson = await predRes.json();

            setCurrentData(currentJson);
            setPrediction(predJson);
            setLastUpdated(new Date());

            // Fetch comparison location (first saved location)
            if (savedLocations.length > 0) {
                const compLoc = savedLocations[0];
                const compRes = await fetch(`${API_URL}/current?lat=${compLoc.lat}&lon=${compLoc.lon}`);
                if (compRes.ok) {
                    const compJson = await compRes.json();
                    setComparisonData({ ...compJson, name: compLoc.name });
                }
            }
        } catch (err) {
            console.error('Fetch error:', err);
            setError('Failed to fetch data');
        }
        setLoading(false);
    }, [API_URL, savedLocations]);

    useEffect(() => {
        if (userLocation) {
            fetchData(userLocation.lat, userLocation.lon);
        }
    }, [userLocation, fetchData]);

    const getAQIClass = (aqi) => {
        if (aqi <= 50) return 'good';
        if (aqi <= 100) return 'moderate';
        if (aqi <= 150) return 'sensitive';
        if (aqi <= 200) return 'unhealthy';
        if (aqi <= 300) return 'very-unhealthy';
        return 'hazardous';
    };

    const getAQIColor = (aqi) => {
        if (aqi <= 50) return '#22c55e';
        if (aqi <= 100) return '#eab308';
        if (aqi <= 150) return '#f97316';
        if (aqi <= 200) return '#ef4444';
        if (aqi <= 300) return '#a855f7';
        return '#991b1b';
    };

    if (loading || isLoadingLocation) {
        return (
            <div className="home-page">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Fetching air quality data...</p>
                </div>
            </div>
        );
    }

    const aqi = currentData?.aqi || 0;
    const category = currentData?.category || 'Unknown';
    const weather = currentData?.weather || {};

    return (
        <div className="home-page">
            {/* Header with Live Badge */}
            <div className="page-header-section">
                <div className="live-indicator">
                    <span className="live-dot"></span>
                    <span>LIVE</span>
                </div>
                <h1 className="location-title">
                    {currentData?.station || locationName} Air Quality Index (AQI) | Air Pollution
                </h1>
                <p className="location-subtitle">
                    Real-time PM2.5, PM10 air pollution level in {locationName}
                </p>
                <p className="last-updated">
                    Last Updated: {lastUpdated?.toLocaleString('en-IN', {
                        dateStyle: 'medium',
                        timeStyle: 'short'
                    })} (Local Time)
                </p>
                <div className="data-source-badge">
                    This is data from <strong>{currentData?.source || 'WAQI'}</strong>
                </div>
            </div>

            {/* Main Content Grid */}
            <div className="main-grid">
                {/* Left Section - AQI Display */}
                <div className="aqi-main-section">
                    <div className="aqi-header-row">
                        <span className="live-aqi-badge">
                            <span className="pulse-dot"></span> Live AQI
                        </span>
                        <span className="aqi-type">Air Quality is</span>
                    </div>

                    <div className="aqi-display-row">
                        <div className="aqi-value-container">
                            <span className={`aqi-value ${getAQIClass(aqi)}`}>{aqi}</span>
                            <span className="aqi-unit">AQI (US)</span>
                        </div>
                        <div className={`aqi-category-badge ${getAQIClass(aqi)}`}>
                            {category}
                        </div>
                    </div>

                    {/* Pollutant Values */}
                    <div className="pollutant-row">
                        <div className="pollutant-item">
                            <span className="poll-label">PM2.5:</span>
                            <span className="poll-value" style={{ color: getAQIColor(aqi) }}>{currentData?.pm25 || 0} ¬µg/m¬≥</span>
                        </div>
                        <div className="pollutant-item">
                            <span className="poll-label">PM10:</span>
                            <span className="poll-value">{currentData?.pm10 || 0} ¬µg/m¬≥</span>
                        </div>
                    </div>

                    {/* AQI Scale */}
                    <AQIScale value={aqi} />

                    {/* Character */}
                    <div className="character-section">
                        <AQICharacter level={aqi} />
                    </div>
                </div>

                {/* Right Section - Weather & More */}
                <div className="info-section">
                    {/* Weather Card */}
                    <div className="weather-card-main">
                        <div className="weather-icon-large">
                            <WeatherIcon weather={weather.weather} />
                        </div>
                        <div className="weather-temp">
                            {weather.temperature || 25}¬∞C
                        </div>
                        <div className="weather-desc">{weather.weather || 'Sunny'}</div>

                        <div className="weather-stats">
                            <div className="stat">
                                <span className="stat-icon">üíß</span>
                                <span className="stat-label">Humidity</span>
                                <span className="stat-value">{weather.humidity || 63}%</span>
                            </div>
                            <div className="stat">
                                <span className="stat-icon">üí®</span>
                                <span className="stat-label">Wind Speed</span>
                                <span className="stat-value">{weather.wind_speed || 14} km/h</span>
                            </div>
                            <div className="stat">
                                <span className="stat-icon">‚òÄÔ∏è</span>
                                <span className="stat-label">UV Index</span>
                                <span className="stat-value">{weather.uv_index || 0}</span>
                            </div>
                        </div>
                    </div>

                    {/* Comparison Card */}
                    {comparisonData && (
                        <div className="comparison-card">
                            <div className="comparison-value">
                                <span className="comp-multiplier">
                                    {(aqi / (comparisonData.aqi || 1)).toFixed(3)}x
                                </span>
                                <span className="comp-label">
                                    {aqi > comparisonData.aqi ? 'Above' : 'Below'}
                                </span>
                            </div>
                            <div className="comparison-text">
                                AQI (US) in {currentData?.station?.split(',')[0] || locationName} is{' '}
                                {(aqi / (comparisonData.aqi || 1)).toFixed(3)} times{' '}
                                {aqi > comparisonData.aqi ? 'Above' : 'Below'} than in {comparisonData.name}
                            </div>
                        </div>
                    )}

                    {/* Prediction Card */}
                    <div className={`prediction-card ${getAQIClass(prediction?.AQI_Prediction || aqi)}`}>
                        <div className="pred-header">
                            <span>üîÆ</span> Next Hour Prediction
                        </div>
                        <div className="pred-value">{prediction?.AQI_Prediction || aqi}</div>
                        <div className="pred-category">{prediction?.category || category}</div>
                        <div className="pred-source">
                            {prediction?.model_trained ? 'üß¨ GA-KELM Model' : 'üìä EPA Formula'}
                        </div>
                    </div>
                </div>
            </div>

            {/* Pollutants Detail Grid */}
            <div className="pollutants-grid">
                <h3>Pollutant Levels</h3>
                <div className="poll-cards">
                    <div className="poll-card">
                        <span className="poll-name">PM2.5</span>
                        <span className="poll-val">{currentData?.pm25 || 0}</span>
                        <span className="poll-unit">¬µg/m¬≥</span>
                    </div>
                    <div className="poll-card">
                        <span className="poll-name">PM10</span>
                        <span className="poll-val">{currentData?.pm10 || 0}</span>
                        <span className="poll-unit">¬µg/m¬≥</span>
                    </div>
                    <div className="poll-card">
                        <span className="poll-name">NO‚ÇÇ</span>
                        <span className="poll-val">{currentData?.no2 || 0}</span>
                        <span className="poll-unit">¬µg/m¬≥</span>
                    </div>
                    <div className="poll-card">
                        <span className="poll-name">O‚ÇÉ</span>
                        <span className="poll-val">{currentData?.o3 || 0}</span>
                        <span className="poll-unit">¬µg/m¬≥</span>
                    </div>
                    <div className="poll-card">
                        <span className="poll-name">SO‚ÇÇ</span>
                        <span className="poll-val">{currentData?.so2 || 0}</span>
                        <span className="poll-unit">¬µg/m¬≥</span>
                    </div>
                    <div className="poll-card">
                        <span className="poll-name">CO</span>
                        <span className="poll-val">{currentData?.co || 0}</span>
                        <span className="poll-unit">mg/m¬≥</span>
                    </div>
                </div>
            </div>

            {/* Quick Actions */}
            <div className="quick-actions">
                <button className="action-btn refresh" onClick={() => userLocation && fetchData(userLocation.lat, userLocation.lon)}>
                    üîÑ Refresh Data
                </button>
