import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useLocationData } from '../App';
import './HomePage.css';

function HomePage() {
    const { userLocation, locationName, isLoadingLocation, API_URL } = useLocationData();
    const [data, setData] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('AQI');

    useEffect(() => {
        if (userLocation) {
            fetchData();
        }
    }, [userLocation]);

    const fetchData = async () => {
        setLoading(true);
        try {
            const [currentRes, predRes] = await Promise.all([
                fetch(`${API_URL}/current`),
                fetch(`${API_URL}/predict`)
            ]);
            setData(await currentRes.json());
            setPrediction(await predRes.json());
        } catch (err) {
            console.error('Error:', err);
        }
        setLoading(false);
    };

    const getAQIClass = (aqi) => {
        if (aqi <= 50) return 'good';
        if (aqi <= 100) return 'moderate';
        if (aqi <= 150) return 'sensitive';
        if (aqi <= 200) return 'unhealthy';
        if (aqi <= 300) return 'very-unhealthy';
        return 'hazardous';
    };

    const getAQIEmoji = (category) => {
        const emojis = {
            'Good': 'ğŸ˜Š',
            'Moderate': 'ğŸ™‚',
            'Unhealthy for Sensitive Groups': 'ğŸ˜',
            'Unhealthy': 'ğŸ˜·',
            'Very Unhealthy': 'ğŸ¤¢',
            'Hazardous': 'ğŸ’€'
        };
        return emojis[category] || 'ğŸ˜·';
    };

    const getWeatherIcon = (weather) => {
        const icons = {
            'Clear': 'â˜€ï¸',
            'Sunny': 'â˜€ï¸',
            'Clouds': 'â˜ï¸',
            'Cloudy': 'â˜ï¸',
            'Rain': 'ğŸŒ§ï¸',
            'Drizzle': 'ğŸŒ¦ï¸',
            'Thunderstorm': 'â›ˆï¸',
            'Snow': 'â„ï¸',
            'Mist': 'ğŸŒ«ï¸',
            'Fog': 'ğŸŒ«ï¸',
            'Haze': 'ğŸŒ«ï¸'
        };
        return icons[weather] || 'ğŸŒ¤ï¸';
    };

    if (isLoadingLocation || loading) {
        return (
            <div className="loading">
                <div className="spinner"></div>
                <p>Loading air quality data...</p>
            </div>
        );
    }

    if (!data) {
        return (
            <div className="error-state">
                <h2>Unable to load data</h2>
                <button onClick={fetchData} className="btn btn-primary">Retry</button>
            </div>
        );
    }

    const weather = data.weather || {};
    const aqiClass = getAQIClass(data.aqi);

    return (
        <div className="home-page fade-in">
            {/* Location Header */}
            <div className="location-header">
                <span className="location-pin">ğŸ“</span>
                <h1 className="location-name">{locationName}</h1>
            </div>

            {/* Tab Navigation */}
            <div className="tab-nav">
                {['AQI', 'History', 'PM2.5', 'PM10', 'CO', 'SO2', 'NO2', 'O3'].map(tab => (
                    <button
                        key={tab}
                        className={`tab-btn ${activeTab === tab ? 'active' : ''}`}
                        onClick={() => setActiveTab(tab)}
                    >
                        {tab}
                    </button>
                ))}
            </div>

            {/* Main Dashboard Grid */}
            <div className="dashboard-grid">
                {/* Left: AQI Display */}
                <div className={`aqi-main-card ${aqiClass}`}>
                    <div className="live-badge">
                        <span className="live-dot"></span>
                        Live AQI
                    </div>

                    <div className="aqi-display">
                        <div className="aqi-number">{data.aqi}</div>
                        <div className="aqi-unit">AQI (US)</div>
                    </div>

                    <div className="aqi-status">
                        <span className="status-label">Air Quality is</span>
                        <span className={`status-value ${aqiClass}`}>{data.category}</span>
                    </div>

                    <div className="pollutant-quick">
                        <div className="pollutant-item">
                            <span className="pollutant-label">PM2.5:</span>
                            <span className="pollutant-value">{data.pm25} Î¼g/mÂ³</span>
                        </div>
                        <div className="pollutant-item">
                            <span className="pollutant-label">PM10:</span>
                            <span className="pollutant-value">{data.pm10} Î¼g/mÂ³</span>
                        </div>
                    </div>

                    {/* AQI Scale */}
                    <div className="aqi-scale-bar">
                        <div className="scale-gradient"></div>
                        <div className="scale-labels">
                            <span>Good</span>
                            <span>Moderate</span>
                            <span>Poor</span>
                            <span>Unhealthy</span>
                            <span>Severe</span>
                            <span>Hazardous</span>
                        </div>
                        <div className="scale-numbers">
                            <span>0</span>
                            <span>50</span>
                            <span>100</span>
                            <span>150</span>
                            <span>200</span>
                            <span>300</span>
                            <span>301+</span>
                        </div>
                        <div
                            className="scale-indicator"
                            style={{ left: `${Math.min(data.aqi / 3, 100)}%` }}
                        ></div>
                    </div>

                    {/* Mascot */}
                    <div className="mascot-container">
                        <div className="mascot">{getAQIEmoji(data.category)}</div>
                    </div>
                </div>

                {/* Right Column */}
                <div className="right-column">
                    {/* Weather Card */}
                    <div className="weather-card">
                        <div className="weather-main">
                            <span className="weather-icon">{getWeatherIcon(weather.weather)}</span>
                            <div className="weather-temp">
                                <span className="temp-value">{weather.temperature || 25}</span>
                                <span className="temp-unit">Â°C</span>
                            </div>
                            <span className="weather-desc">{weather.weather || 'Clear'}</span>
                        </div>
                        <div className="weather-details">
                            <div className="weather-detail">
                                <span className="detail-icon">ğŸ’§</span>
                                <span className="detail-label">Humidity</span>
                                <span className="detail-value">{weather.humidity || 65} %</span>
                            </div>
                            <div className="weather-detail">
                                <span className="detail-icon">ğŸ’¨</span>
                                <span className="detail-label">Wind Speed</span>
                                <span className="detail-value">{weather.wind_speed || 10} km/h</span>
                            </div>
                            <div className="weather-detail">
                                <span className="detail-icon">â˜€ï¸</span>
                                <span className="detail-label">UV Index</span>
                                <span className="detail-value">{weather.uv_index || 5}</span>
                            </div>
                        </div>
                    </div>

                    {/* Prediction Card */}
                    <div className={`prediction-card ${getAQIClass(prediction?.AQI_Prediction)}`}>
                        <div className="pred-header">
                            <span className="pred-icon">ğŸ§¬</span>
                            <span className="pred-title">GA-KELM Prediction</span>
                        </div>
                        <div className="pred-value">{prediction ? Math.round(prediction.AQI_Prediction) : '--'}</div>
                        <div className="pred-category">{prediction?.category || 'Loading...'}</div>
                    </div>
                </div>
            </div>

            {/* Major Pollutants Section */}
            <section className="pollutants-section">
                <div className="section-header">
                    <h2>Major Air Pollutants</h2>
                    <span className="section-location">{locationName}</span>
                </div>
                <div className="pollutants-grid">
                    <div className="pollutant-card">
                        <div className="pollutant-icon">ğŸŒ«ï¸</div>
                        <div className="pollutant-info">
                            <span className="pollutant-name">Particulate Matter (PM2.5)</span>
                        </div>
                        <div className="pollutant-reading">
                            <span className="reading-value">{data.pm25}</span>
                            <span className="reading-unit">Î¼g/mÂ³</span>
                        </div>
                    </div>
                    <div className="pollutant-card highlighted">
                        <div className="pollutant-icon">ğŸŒ«ï¸</div>
                        <div className="pollutant-info">
                            <span className="pollutant-name">Particulate Matter (PM10)</span>
                        </div>
                        <div className="pollutant-reading">
                            <span className="reading-value">{data.pm10}</span>
                            <span className="reading-unit">Î¼g/mÂ³</span>
                        </div>
                    </div>
                    <div className="pollutant-card">
                        <div className="pollutant-icon">ğŸ’¨</div>
                        <div className="pollutant-info">
                            <span className="pollutant-name">Carbon Monoxide (CO)</span>
                        </div>
                        <div className="pollutant-reading">
                            <span className="reading-value">{(data.co * 1000).toFixed(0)}</span>
                            <span className="reading-unit">ppb</span>
                        </div>
                    </div>
                    <div className="pollutant-card">
                        <div className="pollutant-icon">ğŸ§ª</div>
                        <div className="pollutant-info">
                            <span className="pollutant-name">Sulfur Dioxide (SO2)</span>
                        </div>
                        <div className="pollutant-reading">
                            <span className="reading-value">{data.so2}</span>
                            <span className="reading-unit">ppb</span>
                        </div>
                    </div>
                    <div className="pollutant-card">
                        <div className="pollutant-icon">ğŸ­</div>
                        <div className="pollutant-info">
                            <span className="pollutant-name">Nitrogen Dioxide (NO2)</span>
                        </div>
                        <div className="pollutant-reading">
                            <span className="reading-value">{data.no2}</span>
                            <span className="reading-unit">ppb</span>
                        </div>
                    </div>
                    <div className="pollutant-card">
                        <div className="pollutant-icon">ğŸŒ</div>
                        <div className="pollutant-info">
                            <span className="pollutant-name">Ozone (O3)</span>
                        </div>
                        <div className="pollutant-reading">
                            <span className="reading-value">{data.o3}</span>
                            <span className="reading-unit">ppb</span>
                        </div>
                    </div>
                </div>
            </section>

            {/* Quick Links */}
            <section className="quick-links">
                <Link to="/current" className="quick-link">
                    <span className="link-icon">ğŸ“Š</span>
                    <span className="link-text">Detailed AQI</span>
                </Link>
                <Link to="/prediction" className="quick-link">
                    <span className="link-icon">ğŸ”®</span>
                    <span className="link-text">ML Prediction</span>
                </Link>
                <button onClick={fetchData} className="quick-link refresh">
                    <span className="link-icon">ğŸ”„</span>
                    <span className="link-text">Refresh Data</span>
                </button>
            </section>
        </div>
    );
}

export default HomePage;
